import React, { useState, useContext, useEffect } from "react";
import "./App.css";
//import { getTheme, getThemeClass } from "./util";
import GlobalContext from "./context/GlobalContext";
import ContextWrapper from "./context/ContextWrapper";
import App from "./App";
import EventModal from "./components/EventModal";
import './themes.css';
import { getAllEvents } from "./services/EventService";
import dayjs from "dayjs";

function DataLoaded(props) {
  const { showEventModal, setShowEventModal } = useContext(GlobalContext);
  console.log("[MAIN] LOADING DATA... " + props.eventList);

  function handleSubmit(e, calendarEvent) {
    e.preventDefault();
    //const eventID = new Date(replaceRange((startDate ? startDate : daySelected.toDate().toISOString()), 11, 16, eventStartTime.valueOf())).valueOf();
    //console.log("HANDLESUBMIT: " + eventID);
    //console.log("SELECTEDEVENT: " + selectedEvent);
    console.log("[EM] CHECKING END DATE: " + startDate);
    console.log("[EM] CHECKING END DATE: " + endDate);
    endDate == "" ? console.log("[EM] EMPTY END DATE: " + endDate) : console.log("[EM] NOT EMPTY END DATE: " + endDate);
    /*const calendarEvent2 = {
      id: selectedEvent ? selectedEvent.id : null,
      event_title,
      event_description,
      start_date: startDate ? startDate : daySelected,
      end_date: endDate == "" ? startDate : endDate,
      start_time: eventStartTime,
      end_time: eventEndTime,
      is_full_day_event: false,
      is_reccuring: false,
      created_by: "frontend-dev",
      created_date: dayjs(),
      parent_event_id: null,
      event_label: selectedLabel,
    };*/
    if (selectedEvent) {
      dispatchCalEvent({ type: "update", payload: calendarEvent });
    } else {
      //console.log("[EM] CALENDAR EVENT: " + JSON.parse(calendarEvent));
      //console.log("[EM] CALENDAR EVENT2: " + calendarEvent);
      console.log("[EM] CALENDAR EVENT3: " + JSON.stringify(calendarEvent));
      //const parsedEvent = JSON.stringify(calendarEvent);
      createEvent(calendarEvent).then((response) => {
        console.log("[EM] POSTING EVENT SUCCESS: " + response);
        setDataSubmitted(true);
        //props.setReloadData(!props.reloadData);
      });
      
    }
    setShowEventModal(false);
  }

  if (props.isDataLoaded) {
    console.log("[MAIN] DATA LOADED: " + props.eventList);
    return (
      <React.Fragment>
        {showEventModal && <EventModal reloadData={props.reloadData} setReloadData={props.setReloadData}/>}
        <ContextWrapper eventList={props.eventList} isDataLoaded={props.isDataLoaded}>
          <App eventList={props.eventList} isDataLoaded={props.isDataLoaded} handleSubmit={handleSubmit}/>
        </ContextWrapper>
      </React.Fragment>
    );
  }
  console.log("[MAIN] DATA NOT LOADED YET" + props.eventList);
  return (
    <React.Fragment>
      {showEventModal && <EventModal reloadData={props.reloadData} />}
      <ContextWrapper eventList={props.eventList}>
      </ContextWrapper>
    </React.Fragment>
  );
}

function Main(props) {
  //const [currenMonth, setCurrentMonth] = useState(getMonth());
  const { loadData } = useContext(GlobalContext);
  const [eventList, getEvents] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [reloadData, setReloadData] = useState(false);

  //const themeColor = getTheme();
  //const themeClass = getThemeClass();

  console.log("[MAIN] INIT MAIN: " + dayjs() + " " + dataLoaded);

  useEffect(() => {
    if (dataLoaded) {
      console.log("[MAIN] DATA LOADED IN MAIN.EFFECT: " + dataLoaded);
    }
  }, [dataLoaded]);

  useEffect(() => {
    console.log("[MAIN] MAIN.EFFECT LOADING EVENTS: " + dataLoaded);
    getAllEvents().then((response) => {
      const allEvents = response
      //localStorage.setItem("savedEvents", JSON.stringify(allEvents));
      getEvents(allEvents);
      setDataLoaded(true);
    });
  }, [reloadData]);

  return (
    <DataLoaded isDataLoaded={dataLoaded} eventList={eventList} setReloadData={setReloadData} reloadData={reloadData}/>
  );
}

export default Main;
