import React, { useState, useContext, useEffect } from "react";
import "./App.css";
import { getMonth } from "./util";
//import { getTheme, getThemeClass } from "./util";
import CalendarHeader from "./components/CalendarHeader";
import Sidebar from "./components/Sidebar";
import Month from "./components/Month";
import GlobalContext from "./context/GlobalContext";
import EventModal from "./components/EventModal";
import './themes.css';
import { getAllEvents, getAllEvents2 } from "./services/EventService";
import dayjs from "dayjs";
import axios from 'axios';

function useForceUpdate(){
  const [value, setValue] = useState(0); // integer state
  return () => setValue(value => value + 1); // update state to force render
  // An function that increment 👆🏻 the previous state like here 
  // is better than directly setting `value + 1`
}

function App() {
  const [currenMonth, setCurrentMonth] = useState(getMonth());
  const { monthIndex, showEventModal, dataLoaded, setDataLoaded } = useContext(GlobalContext);
  //const themeColor = getTheme();
  //const themeClass = getThemeClass();
  const forceUpdate = useForceUpdate();
  //const [eventList, getEvents] = useState([]);
  useEffect(() => {
    setCurrentMonth(getMonth(monthIndex));
  }, [monthIndex]);

  useEffect(() => {
    setDataLoaded(true);
    console.log("DATA LOADED IN APP: " + dataLoaded);
    dataLoaded ? forceUpdate():console.log("loading in app...");
  }, [dataLoaded]);

  const [eventList, getEvents] = useState([]);
  useEffect(() => {
    getAllEvents();
    console.log(" LOADING APP EFFECTS...");
    console.log(eventList);
  }, []);

  useEffect(() => {
    console.log(" EVENTLIST UPDATED: " + eventList);
  }, [eventList]);

  const getAllEvents = () => {
    axios.get('http://localhost:8000/events')
    .then((response) => {
        const allEvents = response.data
        console.log("LOADED DATA: " + allEvents);
        getEvents(allEvents);
        console.log("COMPLETE TIME: " + dayjs());
    })
    .catch(function(err) {
        console.log("ERROR: " + err); 
    });
  console.log("LOADED??? " + eventList);
  }; 

  return (
    <React.Fragment>
      {showEventModal && <EventModal />}

      <div className={"h-screen flex flex-col theme-red"}>
        <CalendarHeader />
        <div className="flex flex-1">
          <Sidebar />
          <Month month={currenMonth} isDataLoaded={dataLoaded} forceUpdate={forceUpdate} eventList={eventList}/>
        </div>
      </div>
    </React.Fragment>
  );
}

export default App;
