import React, {
  useContext,
  useState,
  useEffect,
  useReducer,
  useMemo,
} from "react";
import GlobalContext from "./GlobalContext";
import { getAllEvents } from "../services/EventService";
import dayjs from "dayjs";
import axios from 'axios';
import Day from "../components/Day";
//import api from "../services/api";

function savedEventsReducer(state, { type, payload }) {
  console.log("[CW] STATE: " + state);
  console.log("[CW] TYPE: " + type);
  console.log("[CW] PAYLOAD: " + payload);
  switch (type) {
    case "push":
      return [...state, payload];
    case "update":
      return state.map((evt) =>
        evt.id === payload.id ? payload : evt
      );
    case "load":
      return payload;
    case "delete":
      return state.filter((evt) => evt.id !== payload.id);
    default:
      throw new Error();
  }
}

function loadData() {
  const [event_list, setEvents] = useState([]);

  useEffect(() => {
    setEvents((event_list) => event_list);

  }, []); // <- add empty brackets here

  return { event_list };
}


/*function loadedEventsReducer(state, { type, payload }) {
  return getAllEvents();
}*/

function initEvents(props) {
  console.log("[CW] INIT EVENTS: " + props);
  //let eventsLoaded = getAllEvents();
  //StorageEvent
  //console.log("[CW] EVENTS LOADED: " + eventsLoaded);
  const storageEvents = props.eventList;
  //const storageEvents = localStorage.getItem("savedEvents");
  //const storageEvents = axios.get('http://localhost:8000/events');
  /*getAllEvents().then((eventdata) => {
    console.log("EVENT DATA: " + eventdata);
    //dispatchCalEvent2({ type: "push", payload: eventdata });
  });*/
  //let event_list = getAllEvents().then((event_data) => {
  //this.setState({ savedEvents: event_data});
  //dispatchCalEvent({ type: "load", payload: event_data });
  //});
  //const storageEvents2 = getAllEvents();
  //console.log("LOADEDEVENTS " + event_list);
  //console.log("LOADEDEVENTS " + loadedEvents);
  //console.log("STORAGEEVENTS " + storageEvents);

  // ðŸ‘‡ï¸ Check if undefined or null
  var count = 0;
  if (props.eventList === undefined || props.eventList === null) {
    console.log('âœ… variable is undefined or null init()');
  } else {
    console.log('â›”ï¸ variable is NOT undefined or null init()');
    count = Object.keys(props.eventList).length;
  };
  const parsedEvents = (count > 0 ? JSON.parse(storageEvents) : []);
  //const parsedEvents = (storageEvents !== undefined ? JSON.parse(storageEvents) : []);
  console.log("[CW] PARSEDEVENTS " + parsedEvents);
  //localStorage.setItem("savedEvents", JSON.stringify(savedEvents));
  //localStorage.setItem("savedEvents", JSON.stringify(getAllEvents()));
  //localStorage.setItem("savedEvents", getAllEvents());
  console.log("[CW] THE GHOST AFFECTED: ");
  return parsedEvents;
}

function useForceUpdate() {
  const [value, setValue] = useState(0); // integer state
  return () => setValue(value => value + 1); // update state to force render
  // An function that increment ðŸ‘†ðŸ» the previous state like here 
  // is better than directly setting `value + 1`
}

export default function ContextWrapper(props) {
  const [monthIndex, setMonthIndex] = useState(dayjs().month());
  const [smallCalendarMonth, setSmallCalendarMonth] = useState(null);
  const [daySelected, setDaySelected] = useState(dayjs());
  const [showEventModal, setShowEventModal] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [labels, setLabels] = useState([]);
  //const [dataLoaded, setDataLoaded] = useState(false);
  //const [forceUpdate, useForceUpdate] = useState(false);
  //const forceUpdate = useForceUpdate();
  //const [event_list, setsavedEvents] = useState([]);
  //const [loadedEvents, dispatchCalEvent2] = useState([]);
  //const [savedEvents, dispatchCalEvent] = useState(initEvents());
  const [eventList, getEvents] = useState(props.eventList);
  const [savedEvents, dispatchCalEvent] = useReducer(
    savedEventsReducer,
    [],
    initEvents
  );
  const {
    setsavedEvents,
  } = useContext(GlobalContext);
  //getAllEvents();
  //const [savedEvents, initEvents] = useState([], );
  console.log("WHAT IS THIS? " + (props.eventList));

  const filteredEvents = useMemo(() => {
    //console.log("SAVED EVENTS " + JSON.stringify(savedEvents));
    //console.log(labels);
    //console.log("LOADED EVENTS " + allEvents);
    //console.log("[CW] FILTERED EVENTS UPDATER: " + eventList);
    //console.log("[CW] TYPE OF: " + typeof eventList);
    //console.log(eventList);
    //const returnList = JSON.parse(eventList);
    console.log("[CW] PROPS DATATYPE: " + typeof props.eventList);
    props.eventList == null ? console.log("[CW] PROPS CONDITION NULLTYPE [TRUE]: " + props.eventList) : console.log("[CW] PROPS CONDITION NULLTYPE [FALSE]: " + props.eventList);
    props.eventList == undefined ? console.log("[CW] PROPS CONDITION UNDEFINED [TRUE]: " + props.eventList) : console.log("[CW] PROPS CONDITION UNDEFINED [FALSE]: " + props.eventList);
    props.eventList == "" ? console.log("[CW] PROPS CONDITION EMPTYSTRING [TRUE]: " + props.eventList) : console.log("[CW] PROPS CONDITION EMPTYSTRING [FALSE]: " + props.eventList);

    var count = Object.keys(props.eventList).length;
    //if (props.eventList. !== "" || props.eventList !==null) {
    if (count >= 0) {
      console.log("[CW] PROPS DATA HOE: " + props.eventList);
      return savedEvents.filter((evt) =>
        //props.eventList ? console.log("[CW] PROPS FOUND: " + props.eventList) : return null;
        //return props.eventList.filter((evt) =>
        labels
          .filter((lbl) => lbl.checked)
          .map((lbl) => lbl.label)
          .includes(evt.event_label)
      );
    };
    return [];

  }, [savedEvents, labels, props.isDataLoaded]);

  /*useEffect(() => {
    getAllEvents().then(
      result => setDataLoaded(true)
    );
    console.log("[CW] LOADING APP EFFECTS...");
    console.log(eventList);
  }, []);*/

  useEffect(() => {
    console.log("[CW] DATA LOADED BITCH " + isDataLoaded);
  }, [isDataLoaded]);


  /*useEffect(() => {
    console.log("[CW] EVENTLIST UPDATED: " + eventList);
  }, [eventList]);*/

  /*const getAllEvents = () => {
    axios.get('http://localhost:8000/events')
    .then((response) => {
        const allEvents = JSON.stringify(response.data);
        localStorage.setItem("savedEvents", allEvents);
        console.log("[CW] LOADED DATA: " + allEvents);
        getEvents(allEvents);
        console.log("[CW] COMPLETE TIME: " + dayjs());
    })
    .catch(function(err) {
        console.log("ERROR: " + err); 
    });
  console.log("[CW] LOADED??? " + eventList);
  }; */

  useEffect(() => {
    //localStorage.setItem("savedEvents", JSON.stringify(savedEvents));
    const storageEvents = localStorage.getItem("savedEvents");
    //localStorage.setItem("savedEvents", JSON.stringify(getAllEvents()));
    //dispatchCalEvent({ type: "load", payload: storageEvents });
    //console.log("[CW] THE GHOST EFFECT: " + storageEvents);
    console.log("[CW] THE GHOST EFFECT 2: " + props.eventList);
    //console.log("[CW] THE GHOST EFFECTOR: " + JSON.parse(storageEvents));

    if (props.isDataLoaded) {
      console.log("[CW] DATA LOADED: " + props.isDataLoaded);
    } else {
      console.log("[CW] DATA NOT LOADED: " + props.isDataLoaded);
    }


    /*if (dataLoaded && eventList != "" && eventList != [] && eventList != undefined && eventList != null) {
      console.log("[CW] DATA LOADED! " + eventList);
      
    } else if (eventList != "" && eventList != [] && eventList != undefined && eventList != null) {
      //data has loaded, re-render page
      console.log("[CW] EVENTS LOADED: " + eventList);
      //savedEvents = storageEvents;
      dispatchCalEvent({ type: "update", payload: eventList });
      setDataLoaded(true);
      //useForceUpdate(true);
    } else {
      console.log("[CW] loading...");
      dispatchCalEvent({ type: "update", payload: eventList });
    };*/

  }, [savedEvents]);

  /*useEffect(() => {
    //localStorage.setItem("savedEvents", JSON.stringify(savedEvents));
    //localStorage.setItem("savedEvents", JSON.stringify(getAllEvents()));
    //localStorage.setItem("savedEvents", getAllEvents());
    console.log("[CW] THE PROPS EFFECT");
  }, [localStorage.getItem("savedEvents")]);*/

  useEffect(() => {
    savedEvents == null ? console.log("[CW] PROPSEFFECT CONDITION EMPTYSTRING [TRUE]: " + savedEvents) : console.log("[CW] PROPSEFFECT CONDITION EMPTYSTRING [FALSE]: " + savedEvents);
    if (savedEvents != null){
      setLabels((prevLabels) => {
        return [...new Set(savedEvents.map((evt) => evt.event_label))].map(
          (label) => {
            const currentLabel = prevLabels.find(
              (lbl) => lbl.label === label
            );
            return {
              label,
              checked: currentLabel ? currentLabel.checked : true,
            };
          }
        );
      });
    };

  }, [savedEvents]);

  useEffect(() => {
    if (smallCalendarMonth !== null) {
      setMonthIndex(smallCalendarMonth);
    }
  }, [smallCalendarMonth]);

  useEffect(() => {
    if (!showEventModal) {
      setSelectedEvent(null);
    }
  }, [showEventModal]);

  function updateLabel(label) {
    setLabels(
      labels.map((lbl) => (lbl.label === label.label ? label : lbl))
    );
  }

  return (
    <GlobalContext.Provider
      value={{
        monthIndex,
        setMonthIndex,
        smallCalendarMonth,
        setSmallCalendarMonth,
        daySelected,
        setDaySelected,
        showEventModal,
        setShowEventModal,
        dataLoaded,
        setDataLoaded,
        dispatchCalEvent,
        selectedEvent,
        setSelectedEvent,
        savedEvents,
        setLabels,
        labels,
        updateLabel,
        filteredEvents,
      }}
    >
      {props.children}
      {props.isDataLoaded}
    </GlobalContext.Provider>
  );
}
